mapper.select:
SELECT T2.* FROM (SELECT T1.* , FLOOR((ROWNUM - 1) / #{pageSize} + 1 ) AS PAGE FROM ( SELECT COUNT(*) OVER() AS CNT ,SEQ_ID ,TITLE ,TO_CHAR(NOTI_START_DATE, 'YYYY-MM-DD') AS NOTI_START_DATE ,TO_CHAR(NOTI_END_DATE, 'YYYY-MM-DD') AS NOTI_END_DATE ,DECODE(NOTI_FLAG, 'Y', '사용', '비사용') AS NOTI_FLAG ,NOTI_FLAG AS NOTI_FLAG_TEST ,NOTI_FLAG AS NOTI_FLAG_CHANGED ,CASE WHEN SYSDATE >= LAST_UPDATE_DATE + 7 THEN 'N' ELSE 'Y' END AS NEW_IMG FROM ITSM_IS_NOTICE_INFO WHERE 1=1
AND TITLE LIKE '%' || #{p_title} || '%'
AND NOTI_FLAG = #{p_noti_flag}
AND NOTI_START_DATE >= TO_DATE(#{p_start_date,jdbcType=VARCHAR},'YYYY-MM-DD')
AND NOTI_END_DATE < TO_DATE(#{p_end_date,jdbcType=VARCHAR},'YYYY-MM-DD')+1
) T1 ) T2 WHERE PAGE = #{curPageNo}

mapper.insert:
	INSERT
	  INTO ITSM_IS_NOTICE_INFO(
	  	   SEQ_ID
  		 , TITLE 
	     , CONTENTS
	     , NOTI_FLAG 
	     , NOTI_START_DATE 
	     , NOTI_END_DATE
	     , CREATE_DATE 
	     , CREATED_BY
	     , LAST_UPDATE_DATE
	     , LAST_UPDATE_BY
		 )
	VALUES(
		   #{seq_id}
		 , #{p_title}
	     , #{p_contents}
	     , #{p_noti_flag}
	     ,TO_DATE(REPLACE(#{p_noti_start_date},'-') || '000001', 'YYYY-MM-DD HH24MISS')
	     ,TO_DATE(REPLACE(#{p_noti_end_date},'-') || '235959', 'YYYY-MM-DD HH24MISS')
	     , SYSDATE
	     , #{loginId}
	     , SYSDATE
	     , #{loginId}
	     )

mapper.update:
UPDATE ITSM_IS_NOTICE_INFO
   SET TITLE            = #{p_title}
      ,CONTENTS         = #{p_contents}
      ,NOTI_FLAG        = #{p_noti_flag}
      ,NOTI_START_DATE  = TO_DATE(REPLACE(#{p_noti_start_date},'-') || '000001', 'YYYY-MM-DD HH24MISS')
      ,NOTI_END_DATE    = TO_DATE(REPLACE(#{p_noti_end_date},'-') || '235959', 'YYYY-MM-DD HH24MISS')
      ,LAST_UPDATE_DATE = SYSDATE
      ,LAST_UPDATE_BY   = #{loginId}
 WHERE SEQ_ID = #{p_seq_id}
