mapper.select:
SELECT T2.* FROM (SELECT T1.* , FLOOR((ROWNUM - 1) / #{pageSize} + 1 ) AS PAGE FROM ( SELECT COUNT(*) OVER() AS CNT ,SEQ_ID ,(SELECT NAME FROM ITSM_COM_USER_INFO WHERE SEQ_ID = ICUI_SEQ_ID) AS USER_NAME ,TITLE ,HIT_COUNT ,(SELECT COUNT(*) FROM ITSM_IS_BBS_REPLY_INFO A WHERE A.BOARD_ID = IIBI.SEQ_ID) AS REPLY_COUNT ,LAST_UPDATE_BY ,TO_CHAR(LAST_UPDATE_DATE, 'YYYY-MM-DD') AS LAST_UPDATE_DATE ,CASE WHEN SYSDATE >= LAST_UPDATE_DATE + 7 THEN 'N' ELSE 'Y' END AS NEW_IMG FROM ITSM_IS_BBS_INFO IIBI WHERE 1=1
AND TITLE LIKE '%' || #{p_title} || '%'
ORDER BY LAST_UPDATE_DATE DESC ) T1 ) T2 WHERE PAGE = #{curPageNo}

mapper.update:
  		MERGE INTO ITSM_IS_BBS_INFO C
			USING DUAL
			ON (SEQ_ID = #{seq_id})
			WHEN MATCHED THEN
			  UPDATE SET C.TITLE            = #{p_title}
				        ,C.CONTENTS         = #{p_content,	jdbcType=CLOB}
				        ,C.LAST_UPDATE_DATE = SYSDATE
				        ,C.LAST_UPDATE_BY   = #{loginId}
			WHEN NOT MATCHED THEN
			  INSERT
			    (SEQ_ID
			    ,ICUI_SEQ_ID
			    ,TITLE
			    ,CONTENTS
			    ,HIT_COUNT
			    ,CREATE_DATE
			    ,CREATED_BY
			    ,LAST_UPDATE_DATE
			    ,LAST_UPDATE_BY)
			  VALUES
			    (#{seq_id}
			    ,#{p_icui_seq_id}
			    ,#{p_title}
			    ,#{p_content,	jdbcType=CLOB}
			    ,'0'
			    ,SYSDATE
			    ,#{loginId}
			    ,SYSDATE
			    ,#{loginId}
			    )

UPDATE ITSM_IS_BBS_INFO
   SET TITLE            = #{p_title}
      ,CONTENTS         = #{p_content}
      ,LAST_UPDATE_DATE = SYSDATE
      ,LAST_UPDATE_BY   = #{loginId}
 WHERE SEQ_ID = #{p_seq_id}

    	UPDATE ITSM_IS_BBS_INFO
		   SET HIT_COUNT            = HIT_COUNT + 1
		 WHERE SEQ_ID = #{p_seq_id}
